create table journal_entries ( id uuid default uuid_generate_v4() primary key, user_id uuid references auth.users(id) not null, type text not null check (type in ('progress', 'daily', 'review', 'list')), game_id text, title text, content text, progress text, hours_played integer, rating integer check (rating >= 1 and rating <= 10), cover_url text, created_at timestamp with time zone default timezone('utc'::text, now()) not null, updated_at timestamp with time zone default timezone('utc'::text, now()) not null);
